mapper.select:
SELECT no, userid, username, to_char(payStart, 'YYYY-MM-DD') as payStart, to_char(payEnd, 'YYYY-MM-DD') as payEnd, payMethod, refund, grade FROM (select * from (select * from (select * from (SELECT pay.no, pay.userid, pay.username, pay.payStart, pay.payEnd, pay.payMethod, pay.refund, m.grade FROM pay pay, (SELECT userid, grade FROM member) m WHERE pay.userid = m.userid) where ${pagingVO.searchKey} like '%${pagingVO.searchWord}%' and
where payStart BETWEEN to_date('${payVO.selectStartDate} 00:00:00', 'YYYY-MM-DD HH24:MI:SS') and to_date('${payVO.selectEndDate} 23:59:59', 'YYYY-MM-DD HH24:MI:SS') payStart BETWEEN to_date('${payVO.selectStartDate}-1 00:00:00', 'YYYY-MM-DD HH24:MI:SS') and to_date(to_char(LAST_DAY(to_date('${payVO.selectEndDate}', 'YYYY-MM')), 'YYYY-MM-DD')||' 23:59:59' ,'YYYY-MM-DD HH24:MI:SS') payStart BETWEEN to_date('${payVO.selectStartDate}-1-1 00:00:00', 'YYYY-MM-DD HH24:MI:SS') and to_date(to_char(LAST_DAY(to_date('${payVO.selectEndDate}-12', 'YYYY-MM')), 'YYYY-MM-DD')||' 23:59:59' ,'YYYY-MM-DD HH24:MI:SS')
) order by no desc) where rownum<=${pagingVO.pageNum * pagingVO.onePageRecode} order by no asc) where rownum<=${pagingVO.lastPageRecode} ${pagingVO.onePageRecode} ORDER BY no DESC)
